import React from "react";
import "./Calendar.css";

//Fullcalendar and Realted Plugins
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import interactionPlugin from "@fullcalendar/interaction"; 
import listPlugin from '@fullcalendar/list'; //For List View
//moment
import momentPlugin from "@fullcalendar/moment";
// ES6 Modules 
import Swal from "sweetalert2"

function eventResize(event, delta, revertFunc) {
  var endDate = event.end.format().toString();
  var startDate = event.start.format().toString();
}

function Calendar() {
  
  return (
<div className="maincontainer">
    <FullCalendar
      plugins={[momentPlugin, dayGridPlugin, interactionPlugin, listPlugin ]}
      initialView="dayGridMonth"
      editable= "true"
      droppable= "true"
      headerToolbar={{
        left: 'prev,next today',
        center: 'title',
        right: 'dayGridMonth,dayGridWeek,dayGridDay,listWeek',
        //center: 'addEventButton'
      }}

      /*eventClick={
        function(arg){
          alert("Hello "+ arg.event.title)
          alert(arg.event.start)
        }
      }*/
      eventClick={function(arg) {
        eventResize= eventResize
          Swal.fire({
               title: 'Event',
               showDenyButton: true,
               showCancelButton: true,
               confirmButtonText: 'Edit Event',
               denyButtonText: 'Delete Event',
               cancelButtonText: 'Cancel',
          }).then((result) => {
                  /* Read more about isConfirmed, isDenied below */
            if (result.isConfirmed) {
              Swal.fire({
              title: 'Enter your new event',
               input: 'text',
               inputPlaceholder: 'Enter your new event',
               showCancelButton: true,
            })
            } else if (result.dismiss === Swal.DismissReason.deny) {
                    Swal.fire(
                    'Deleted!',
                    'Your file has been deleted.',
                    'success'
                  )
                }
          })
      }}
        //initialView="dayGridMonth"

      events={[
        { title: 'event 1', date: '2021-02-13' },
        { title: 'event 2', date: '2021-02-15' },
        { title: 'event 2', date: '2021-01-15' },
        { title: 'event 2', date: '2021-02-15' },
        { title: 'event 2', date: '2021-02-15' },
        { title: 'event 2', date: '2021-02-15' },
        { title: 'event 2', date: '2021-02-15' }
      ]}
    />
</div>
);
}

export default Calendar;
