import React, {useEffect} from "react";
import "./Calendar.css";

//Fullcalendar and Realted Plugins
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import interactionPlugin from "@fullcalendar/interaction"; 
import listPlugin from '@fullcalendar/list'; //For List View
import googleCalendarPlugin from "@fullcalendar/google-calendar";
//moment

import momentPlugin from "@fullcalendar/moment";
// ES6 Modules 
import Swal from "sweetalert2";

function eventResize(event, delta, revertFunc) {
  var endDate = event.end.format().toString();
  var startDate = event.start.format().toString();
}

function Calendar() {
  //var gCalId =   'en.lb#holiday@group.v.calendar.google.com'
  //var gApi = 'AIzaSyB9zqr0fs3irj_MKtwOx1AIdQfatLj6mgo'
  useEffect(()=>{
    axios.get('http://localhost:8081/user').then((res)=>{
          const responseUser = res.data;
          setUser(responseUser);
    });
}, []);
  return (
<div className="maincontainer">
    <FullCalendar
      plugins={[dayGridPlugin, interactionPlugin, listPlugin,googleCalendarPlugin  ]}
      timeFormat= "dddd, MMMM Do YYYY, h:mm:ss a"

      initialView="dayGridMonth"
      editable= "true"
      droppable= "true"
      headerToolbar={{
        left: 'prev,next today',
        center: 'title',
        right: 'dayGridMonth,dayGridWeek,dayGridDay,listWeek',
        //center: 'addEventButton'
      }}
     // googleCalendarApiKey= 'AIzaSyB9zqr0fs3irj_MKtwOx1AIdQfatLj6mgo'
      /*eventClick={
        function(arg){
          alert("Hello "+ arg.event.title)
          alert(arg.event.start)
        }
      }*/
      eventClick={function(arg) {
        eventResize= eventResize
          Swal.fire({
               title: 'Event',
               showDenyButton: true,
               showCancelButton: true,
               confirmButtonText: 'Edit Event',
               denyButtonText: 'Delete Event',
               cancelButtonText: 'Cancel',
               initialDate: '2021-02-12',
                businessHours: true, // display business hours
                editable: true,
          }).then((result) => {
                  /* Read more about isConfirmed, isDenied below */
            if (result.isConfirmed) {
              Swal.fire({
              title: 'Enter your new event',
               input: 'text',
               inputPlaceholder: 'Enter your new event',
               showCancelButton: true,
            })
            } else if (result.dismiss === Swal.DismissReason.deny) {
                    Swal.fire(
                    'Deleted!',
                    'Your file has been deleted.',
                    'success'
                  )
                }
          })
      }}
     // AIzaSyDOHFBph3LTDnDGTldEghP89e0Vp3yczZQ
        //initialView="dayGridMonth"
        //AIzaSyDOHFBph3LTDnDGTldEghP89e0Vp3yczZQ
        //AIzaSyCFKUuwVor86ZdXFN8yKNV0LtruWsVNmh0
        googleCalendarApiKey='AIzaSyCFKUuwVor86ZdXFN8yKNV0LtruWsVNmh0'
      events={[
        { title: 'event 1', date: '2021-02-13' ,},
        { title: 'event 2', date: '2021-02-15' },
        { title: 'event 2',  start: '2021-02-03T13:00:00' , constraint: 'businessHours'},
        { title: 'event 2', date: '2021-02-15' },
        { title: 'event 2', date: '2021-02-15' },
        { title: 'event 2', date: '2021-02-15' },
        { title: 'event 2', date: '2021-02-15' },
        { googleCalendarId: 'ziadsobh70@gmail.com', },
        {
        start: '2021-02-10',
        end: '2021-02-12',
        overlap: false,
        display: 'background',
        color: '#ff9f89'
      },
      {
        title: 'Meeting',
        start: '2021-02-13T11:00:00',
        constraint: 'availableForMeeting', // defined below
        color: '#257e4a'
      },
      {
        groupId: 'availableForMeeting',
        start: '2021-02-13T10:00:00',
        end: '2021-02-13T16:00:00',
        display: 'background'
      },
      ]} 
        
    />
</div>
);
}

export default Calendar;
